public class Main {    public static void main(String[] args) {        System.out.println("Задача N1");        var dog = 8;        System.out.println(dog);        var cat = 3.6;        System.out.println(cat);        var paper = 763789;        System.out.println(paper);        System.out.println("Задача N2");        dog = dog + 4;        System.out.println(dog);        cat = cat + 4;        System.out.println(cat);        paper = paper + 4;        System.out.println(paper);        System.out.println("Задача N3");        var dog1 = 8.0;        dog1 = dog1 - 3.5;        System.out.println(dog1);        cat = cat - 1.6;        System.out.println(cat);        paper = paper - 7639;        System.out.println(paper);        System.out.println("Задача N4");        var friend = 19;        System.out.println(friend);        friend = friend + 2;        System.out.println(friend);        friend = friend/7;        System.out.println(friend);        System.out.println("Задача N5");        var frog = 3.5;        System.out.println(frog);        frog = frog * 10;        System.out.println(frog);        frog = frog / 3.5;        System.out.println(frog);        frog = frog + 4;        System.out.println(frog);        System.out.println("Задача N6");        var boxer1Weight = 78.2;        var boxer2Weight = 82.7;        var fullWeight = boxer1Weight + boxer2Weight;        System.out.println("Общий вес боксеров " + fullWeight);        var differenceWeight = boxer2Weight - boxer1Weight;        System.out.println("Разница между весом боксеров " + differenceWeight);        System.out.println("Задача N7");        System.out.println("Разница между весом боксеров (вариант1) " + (boxer2Weight - boxer1Weight));        System.out.println("Разница между весом боксеров (вариант2) " + (boxer2Weight % boxer1Weight));        System.out.println("Задача N8");        var totalHours = 640;        var hours1Man = 8;        System.out.println("Всего работников в компании - " + totalHours / hours1Man + " человек.");        System.out.println("Если в компании работает " + (totalHours / hours1Man + 94) + " человека, то всего по " + totalHours / (totalHours / hours1Man + 94) + " часа работы может быть поделено между сотрудниками.");    }}